                            Bạn chưa có tài khoản? Vui lòng <a href="javascript:;" class="loginForm_close slide_open udl">Register</a>
                        </div>
                        <div class="buton">
                            <a href="javascript:;" onclick="loginMember();" title="Login" class="btnform">Login</a>
                            <a href="javascript:;" onclick="ResetAllTextBox('#loginForm')" title="Content" class="btnform">Content</a>
                        </div>
                    </div>
                    <div class="cb"></div>
                </div>
            </div>
            <a class="btnclose loginForm_close"></a>
            <div class="cb"></div>
        </div>
        <div id="ForgotForm" class="alertpopup">
            <div class="formloginx">
                <div class="title">Quên mật khẩu</div>
                <div class="main">
                    <p class="lh22 fs16">Bạn quên mật khẩu, vui lòng nhập mail để lấy lại mật khẩu</p>
                    <div class="ip">
                        <input name="DisplayLoadControl$Login$TextBox1" type="text" id="DisplayLoadControl_Login_TextBox1" class="required" onkeydown="CheckLogin(event)" placeholder="Nhập email của bạn *" />
                    </div>
                    <div class="buton tac mt5">
                        <a href="javascript:;" onclick="ForgotPass();" title="Gửi" class="btnform">Gửi</a>
                    </div>
                    <div class="cb"></div>
                </div>
            </div>
            <a class="btnclose ForgotForm_close"></a>
            <div class="cb"></div>
        </div>
        <div class="success_open"></div>
        <div id="success" class="alertpopup">
            <div class="title">Thông báo</div>
            <div class="main">
                <p class="tac pb15 fs16">Đăng ký thành công</p>
                <a href="javascript:;" class="success_close">Đồng ý</a>
                <div class="cb"></div>
            </div>
        </div>
        <div id="loginForm2" class="alertpopup">
            <div class="formloginx">
                <div class="title">Xác nhận quyền truy cập thống kê</div>
                <div class="main">
                    <div class="boxbox">
                        <p class="tde">Login</p>
                        <p class="lh22">Vui lòng nhập mật khẩu</p>
                        <div class="ip">
                            <input name="DisplayLoadControl$Login$TextBox2" type="text" id="DisplayLoadControl_Login_TextBox2" class="required" onkeydown="CheckLogin2(event)" onchange="ResetLabel4();" />
                            <label class="note loginnote cRed"></label>
                        </div>
                        <div class="buton">
                            <a href="javascript:;" onclick="loginMember2();" title="Xác nhận" class="btnform">Xác nhận</a>
                        </div>
                    </div>
                    <div class="cb"></div>
                </div>
            </div>
            <a class="btnclose loginForm2_close"></a>
            <div class="cb"></div>
        </div>
        <script type="text/javascript">
            function ResetLabel() {
                if ($("#DisplayLoadControl_Login_tbUser").next().text() !== "Tên đăng nhập cần có ít nhất 8 ký tự. Ví dụ: Mizuiku123") {
                    $("#DisplayLoadControl_Login_tbUser").next().text("Tên đăng nhập cần có ít nhất 8 ký tự. Ví dụ: Mizuiku123").removeClass("cRed");
                }
            }

            function ResetLabel2() {
                if ($("#DisplayLoadControl_Login_tbPass").next().text() !== "Mật khẩu cần có ít nhất 8 ký tự bao gồm: Chữ, số, chữ in hoa và kí tự đặc biệt. Ví dụ: Mizuiku12@") {
                    $("#DisplayLoadControl_Login_tbPass").next().text("Mật khẩu cần có ít nhất 8 ký tự bao gồm: Chữ, số, chữ in hoa và kí tự đặc biệt. Ví dụ: Mizuiku12@").removeClass("cRed");
                }
            }

            function ResetLabel3() {
                $("label.loginnote").hide();
            }

            function LoadDistrict(igid, type) {
                loading(true);
                jQuery.ajax({
                    url: weburl + "cms/display/Member/Ajax/Ajax.aspx",
                    type: "POST",
                    dataType: "json",
                    data: {
                        "action": "LoadDistrict",
                        "igid": igid,
                        "type": type
                    },
                    success: function(res) {
                        loading(false);
                        if (type === 1) $("#DisplayLoadControl_Login_ddlDistric").html(res[0]);

                    },
                    error: function(error) { //Lỗi xảy ra
                        loading(false);
                    }
                });
            }

            function ForgotPass() {
                var email = $("#DisplayLoadControl_Login_TextBox1").val();
                if (!CheckEmailValue($("#DisplayLoadControl_Login_TextBox1").val())) {
                    $("#DisplayLoadControl_Login_TextBox1").focus();
                    alert("Bạn đã nhập email chưa chính xác, vui lòng nhập lại email.");
                    return;
                }
                loading(true);
                jQuery.ajax({
                    url: weburl + "cms/display/Member/Ajax/Ajax.aspx",
                    type: "POST",
                    dataType: "json",
                    data: {
                        "action": "ForgotPass",
                        "email": email
                    },
                    success: function(res) {
                        loading(false);
                        if (res[0] === "unActive") {
                            $("#success .tac").html("Tài khoản của bạn chưa được kích hoạt!");
                            $(".success_open").click();
                        } else if (res[0] === "LockUser") {
                            $("#success .tac").html("Tài khoản của bạn đang tạm khóa!");
                            $(".success_open").click();
                        } else if (res[0] === "Error") {
                            $("#success .tac").html("Hệ thống đang bận, bạn vui lòng thử lại sau!");
                            $(".success_open").click();
                        } else {
                            $("#success .tac").html("Reset mật khẩu thành công, vui lòng kiểm tra email để nhận mật khẩu mới!");
                            $(".success_open").click();
                        }
                    },
                    error: function(error) { //Lỗi xảy ra
                        loading(false);
                    }
                });
            }

            function registerMember() {

                var obError = undefined;
                $("#regisForm .required").each(function() {
                    if (obError == undefined && $(this).val() === '') {
                        obError = $(this);
                        return;
                    }
                });
                if (obError != undefined) {
                    obError.focus();
                    return;
                }
                if (!CheckEmailValue($("#DisplayLoadControl_Login_tbEmail").val())) {
                    $("#DisplayLoadControl_Login_tbEmail").focus();
                    alert("Bạn đã nhập email chưa chính xác, vui lòng nhập lại email.");
                    return;
                }
                var account = "",
                    email = "",
                    matkhau = "",
                    hovaten = "",
                    gioitinh = "",
                    sodienthoai = "",
                    chucvu = "",
                    thanhpho = "",
                    quanhuyen = "",
                    noicongtac = "";
                account = $("#DisplayLoadControl_Login_tbUser").val();
                matkhau = $("#DisplayLoadControl_Login_tbPass").val();
                hovaten = $("#DisplayLoadControl_Login_tbName").val();
                gioitinh = $("#DisplayLoadControl_Login_ddlSex option:selected").val();
                email = $("#DisplayLoadControl_Login_tbEmail").val();
                sodienthoai = $("#DisplayLoadControl_Login_tbPhone").val();
                chucvu = $("#DisplayLoadControl_Login_ddlType option:selected").val();
                thanhpho = $("#DisplayLoadControl_Login_ddlCity option:selected").val();
                quanhuyen = $("#DisplayLoadControl_Login_ddlDistric option:selected").val();
                noicongtac = $("#DisplayLoadControl_Login_ddlSchool").val();

                if (gioitinh === "0") {
                    alert('Vui lòng chọn giới tính trước khi đăng ký!');
                    $("#DisplayLoadControl_Login_ddlSex").focus();
                    return;
                }
                if (chucvu === "0") {
                    alert('Vui lòng chọn thông tin trước khi đăng ký!');
                    $("#DisplayLoadControl_Login_ddlType").focus();
                    return;
                }
                if (thanhpho === "0") {
                    alert('Vui lòng chọn Tỉnh/Thành phố trước khi đăng ký!');
                    $("#DisplayLoadControl_Login_ddlCity").focus();
                    return;
                }
                if (quanhuyen === "0") {
                    alert('Vui lòng chọn Quận/Huyện/Thị xã trước khi đăng ký!');
                    $("#DisplayLoadControl_Login_ddlDistric").focus();
                    return;
                }
                if (noicongtac === "0") {
                    alert('Vui lòng chọn nơi công tác trước khi đăng ký!');
                    $("#DisplayLoadControl_Login_ddlSchool").focus();
                    return;
                }
                if (!$('#cb_Accept').is(':checked')) {
                    alert('Vui lòng chọn đồng ý các điều khoản và quy định!');
                    return;
                }
                loading(true);
                jQuery.ajax({
                    url: weburl + "cms/display/Member/Ajax/Ajax.aspx",
                    type: "POST",
                    dataType: "json",
                    data: {
                        "action": "Register",
                        "account": account,
                        "matkhau": matkhau,
                        "hovaten": hovaten,
                        "gioitinh": gioitinh,
                        "email": email,
                        "sodienthoai": sodienthoai,
                        "chucvu": chucvu,
                        "thanhpho": thanhpho,
                        "quanhuyen": quanhuyen,
                        "noicongtac": noicongtac
                    },
                    success: function(res) {
                        loading(false);
                        if (res[0] === "Exists") {
                            $("label.username").html("Email đã được sử dụng, vui lòng chọn tài khoản khác.").addClass("cRed");
                        } else if (res[0] === "SpecialChar") {
                            $("label.username").html("Tên đăng nhập không được chứa ký tự đặc biệt.").addClass("cRed");
                        } else if (res[0] === "Space") {
                            $("label.username").html("Tên đăng nhập và mật khẩu không được có khoảng trống.").addClass("cRed");
                        } else if (res[0] === "LenghtLow") {
                            $("label.username").html("Tên đăng nhập phải có độ dài trên 8 ký tự.").addClass("cRed");
                        } else if (res[0] === "NotSecure") {
                            $("label.passw").html("Mật khẩu phải chứa chữ hoa, chữ thường, số, ký tự đặc biệt và độ dài trên 8 ký tự.").addClass("cRed");
                        } else {
                            ResetAllTextBox("#regisForm");
                            $("#success .tac").html("Đăng ký thành công, vui lòng kiểm tra thư hộp thư đến và hộp thư rác để kích hoạt tài khoản! Trường hợp không nhận được email gửi tới, vui lòng gửi yêu cầu trong mục Liên hệ");
                            $(".success_open").click();
                        }
                    },
                    error: function(error) { //Lỗi xảy ra
                        loading(false);
                        alert("Hệ thống đang bận, bạn vui lòng thử lại sau!");
                    }
                });
            }

            function CheckLogin(e) {
                if (e.keyCode === 13) {
                    loginMember();
                    e.preventDefault();
                }
            }

            function loginMember() {
                var obError = undefined;
                $("#loginForm .required").each(function() {
                    if (obError == undefined && $(this).val() === '') {
                        obError = $(this);
                        return;
                    }
                });
                if (obError != undefined) {
                    obError.focus();
                    return;
                }
                var email = "",
                    matkhau = "",
                    remember = "0";
                email = $("#DisplayLoadControl_Login_tbEmailLogin").val();
                matkhau = $("#DisplayLoadControl_Login_tbPassLogin").val();
                if ($("#RememberPass").prop('checked')) {
                    remember = "1";
                }
                loading(true);
                jQuery.ajax({
                    url: weburl + "cms/display/Member/Ajax/Ajax.aspx",
                    type: "POST",
                    dataType: "json",
                    data: {
                        "action": "Login",
                        "email": email,
                        "matkhau": matkhau,
                        "remember": remember
                    },
                    success: function(res) {
                        loading(false);
                        if (res[0] === "Success") {
                            window.location.href = 'https://mizuiku-emyeunuocsach.vn/photo-library.htm';
                        } else if (res[0] === "unActive") {
                            $("label.loginnote").text("Đăng nhập không thành công, tài khoản chưa được kích hoạt!");
                            $("label.loginnote").css("display", "block");
                        } else if (res[0] === "LockUser") {
                            $("label.loginnote").text("Đăng nhập không thành công, Tài khoản đang bị khóa!");
                            $("label.loginnote").css("display", "block");
                        } else {
                            $("label.loginnote").text("Email hoặc mật khẩu không chính xác!");
                            $("label.loginnote").css("display", "block");
                        }
                    },
                    error: function(error) { //Lỗi xảy ra
                        loading(false);
                        alert("Hệ thống đang bận, bạn vui lòng thử lại sau!");
                    }
                });
            }

            function loginMember2() {
                var obError = undefined;
                $("#loginForm2 .required").each(function() {
                    if (obError == undefined && $(this).val() === '') {
                        obError = $(this);
                        return;
                    }
                });
                if (obError != undefined) {
                    obError.focus();
                    return;
                }
                var pass = "";
                pass = $("#DisplayLoadControl_Login_TextBox2").val();
                loading(true);
                jQuery.ajax({
                    url: weburl + "cms/display/Member/Ajax/Ajax.aspx",
                    type: "POST",
                    dataType: "json",
                    data: {
                        "action": "LoginAnalytics",
                        "pass": pass
                    },
                    success: function(res) {
                        loading(false);
                        if (res[0] === "Success") {
                            window.location.href = 'https://mizuiku-emyeunuocsach.vn/photo-library.htm';
                        } else {
                            $("label.loginnote").text("Mật khẩu không chính xác!");
                            $("label.loginnote").css("display", "block");
                        }
                    },
                    error: function(error) { //Lỗi xảy ra
                        loading(false);
                        alert("Hệ thống đang bận, bạn vui lòng thử lại sau!");
                    }
                });
            }
            $(document).ready(function() {

                $('#slide').popup({
                    focusdelay: 400,
                    outline: true,
                    vertical: 'top'
                });
                $('#loginForm').popup({
                    focusdelay: 400,
                    outline: true,
                    vertical: 'top'
                });
                $('#loginForm2').popup({
                    focusdelay: 400,
                    outline: true,
                    vertical: 'top'
                });
                $('#ForgotForm').popup({
                    focusdelay: 400,
                    outline: true,
                    vertical: 'top'
                });
                $('#success').popup({
                    focusdelay: 400,
                    outline: true,
                    vertical: 'top'
                });
            });

            function openalert() {

            }
        </script>

        <script>
            function setUserName(val) {
                $("#DisplayLoadControl_Login_tbUser").val(val);
            }
        </script>

        <style>
            #slide_background,
            #success_background,
            #loginForm_background,
            #ForgotForm_background {
                -webkit-transition: all 0.3s 0.3s;
                -moz-transition: all 0.3s 0.3s;
                transition: all 0.3s 0.3s;
            }
            
            #slide,
            #slide_wrapper,
            #success,
            #loginForm,
            #ForgotForm,
            #success_wrapper {
                -webkit-transition: all 0.4s;
                -moz-transition: all 0.4s;
                transition: all 0.4s;
            }
        </style>

    </form>
</body>

